calculate_engagement_score.routine_definition:
DECLARE
  score DECIMAL := 0;
  bookings_made INTEGER := 0;
  events_organized INTEGER := 0;
  designer_bookings INTEGER := 0;
BEGIN
  -- Get booking activity
  SELECT COALESCE(COUNT(*), 0) INTO bookings_made
  FROM bookings b
  WHERE b.user_id = user_id_param;

  -- Get events organized
  SELECT COALESCE(COUNT(*), 0) INTO events_organized
  FROM events e
  WHERE e.organizer_id = user_id_param;

  -- Get designer bookings if user is a designer
  SELECT COALESCE(COUNT(*), 0) INTO designer_bookings
  FROM designer_bookings db
  JOIN designer_profiles dp ON db.designer_id = dp.id
  WHERE dp.user_id = user_id_param;

  -- Calculate engagement score (0-100) based on platform activity
  score := LEAST(100, 
    (bookings_made * 10) + 
    (events_organized * 15) +
    (designer_bookings * 8)
  );

  RETURN score;
END;

calculate_event_metrics.routine_definition:
declare
  result jsonb;
  views_count integer;
  registrations_count integer;
  revenue_sum numeric(10,2);
begin
  -- Views
  select count(*) into views_count
  from public.activity_logs
  where entity_id = event_uuid
    and activity_type = 'view_event'
    and date(created_at) = target_date;

  -- Registrations
  select count(*) into registrations_count
  from public.event_registrations
  where event_id = event_uuid
    and date(created_at) = target_date;

  -- Revenue from confirmed orders
  select coalesce(sum(total_amount), 0)::numeric(10,2) into revenue_sum
  from public.orders
  where event_id = event_uuid
    and order_status = 'confirmed'
    and date(created_at) = target_date;

  result := jsonb_build_object(
    'event_id', event_uuid,
    'date', target_date,
    'views', coalesce(views_count, 0),
    'registrations', coalesce(registrations_count, 0),
    'revenue', coalesce(revenue_sum, 0)
  );

  return result;
end;

check_venue_availability.routine_definition:
DECLARE
  conflict_count INTEGER;
BEGIN
  SELECT COUNT(*) INTO conflict_count
  FROM venue_bookings vb
  WHERE vb.venue_id = venue_id_param
    AND vb.booking_date = check_date
    AND vb.status NOT IN ('cancelled', 'declined')
    AND (
      (vb.start_time <= start_time_param AND vb.end_time > start_time_param) OR
      (vb.start_time < end_time_param AND vb.end_time >= end_time_param) OR
      (vb.start_time >= start_time_param AND vb.end_time <= end_time_param)
    );

  RETURN conflict_count = 0;
END;

cleanup_expired_sessions.routine_definition:
declare
  deleted_count integer;
begin
  delete from public.user_sessions
  where expires_at < now() or last_activity < (now() - interval '90 days');

  get diagnostics deleted_count = row_count;
  return deleted_count;
end;

cleanup_old_activity_logs.routine_definition:
declare
  deleted_count integer;
begin
  delete from public.activity_logs
  where created_at < (now() - interval '90 days');

  get diagnostics deleted_count = row_count;
  return deleted_count;
end;

generate_designer_slug.routine_definition:
BEGIN
  IF NEW.brand_slug IS NULL OR NEW.brand_slug = '' THEN
    NEW.brand_slug := lower(regexp_replace(COALESCE(NEW.brand_name,''), '[^a-zA-Z0-9]+', '-', 'g'));
    NEW.brand_slug := btrim(NEW.brand_slug, '-');
    -- Ensure uniqueness
    WHILE EXISTS (SELECT 1 FROM public.designer_profiles WHERE brand_slug = NEW.brand_slug AND id <> COALESCE(NEW.id, gen_random_uuid())) LOOP
      NEW.brand_slug := NEW.brand_slug || '-' || extract(epoch FROM now())::int;
    END LOOP;
  END IF;
  RETURN NEW;
END;

generate_venue_slug.routine_definition:
BEGIN
  IF NEW.slug IS NULL OR NEW.slug = '' THEN
    NEW.slug := lower(regexp_replace(COALESCE(NEW.venue_name,''), '[^a-zA-Z0-9]+', '-', 'g'));
    NEW.slug := btrim(NEW.slug, '-');

    -- Ensure uniqueness
    WHILE EXISTS (
      SELECT 1 FROM public.venues WHERE slug = NEW.slug AND id <> COALESCE(NEW.id, gen_random_uuid())
    ) LOOP
      NEW.slug := NEW.slug || '-' || extract(epoch FROM now())::int;
    END LOOP;
  END IF;
  RETURN NEW;
END;

get_user_role.routine_definition:
DECLARE
  user_role TEXT;
BEGIN
  SELECT role INTO user_role 
  FROM user_roles
  WHERE clerk_user_id = auth.jwt()->>'sub'
  LIMIT 1;
  RETURN COALESCE(user_role, 'customer');
END;

has_role.routine_definition:
select exists (
  select 1 from public.user_roles
  where user_id = _user_id and role = _role
);

is_admin.routine_definition:
BEGIN
  RETURN EXISTS (
    SELECT 1 FROM user_roles
    WHERE clerk_user_id = auth.jwt()->>'sub'
    AND role = 'admin'
  );
END;

